import spacy

nlp = spacy.load('en_core_web_sm')


def slice_semantically(text, length=5000):
    doc = nlp(text)
    slices = []
    slice = []

    for token in doc:
        if len(' '.join(slice + [token.text])) > length:
            slices.append(' '.join(slice))
            slice = []
        slice.append(token.text)
    if slice:
        slices.append(' '.join(slice))
    return slices


